mapper.update:
UPDATE seat_location
seat_id = #{seatLocation.seatId,jdbcType=VARCHAR}, seat_area = #{seatLocation.seatArea,jdbcType=VARCHAR}, seat_storey = #{seatLocation.seatStorey,jdbcType=VARCHAR}, seat_room = #{seatLocation.seatRoom,jdbcType=VARCHAR}, seat_number = #{seatLocation.seatNumber,jdbcType=VARCHAR}, deleted = #{seatLocation.deleted,jdbcType=INTEGER}, seat_building = #{seatLocation.seatBuilding,jdbcType=VARCHAR}
WHERE seat_id = #{seatLocation.seatId,jdbcType=VARCHAR}

mapper.select:
select

from seat_location
where seat_area=#{seatArea,jdbcType=VARCHAR}
    and seat_building=#{seatBuilding,jdbcType=VARCHAR}
    and seat_storey=#{seatStorey,jdbcType=VARCHAR}
    and seat_room=#{seatRoom,jdbcType=VARCHAR}
